import { AuthService } from './../../services/auth.service';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register-form',
  templateUrl: './register-form.component.html',
  styleUrls: ['./register-form.component.css']
})
export class RegisterFormComponent {
registerForm!: FormGroup

constructor(private fb: FormBuilder, private authService: AuthService, private router: Router){
  this.registerForm = this.fb.group({
    name: ['', Validators.required],
    surname: ['', Validators.required],
    email: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.minLength(8), Validators.maxLength(12)]]
  })

}

formHandler() {
  if(this.registerForm.valid){
    const {name, surname, email, password} = this.registerForm.value;
    const objectToSend = {
      name,
      surname,
      email,
      password
    }
    console.log(objectToSend)
    this.authService.register(objectToSend).subscribe({
      next: (response) => {
        this.router.navigate(['login']) 
      },
      error: (error) => {
        //
      }
    })
    this.registerForm.reset() //Llamada a la api
  }
}


}
<form (ngSubmit)="formHandler()" [formGroup]="registerForm">
  <div class="form-group">
    <label for="name">Nombre:</label>
    <input type="text" id="name" name="name" formControlName="name" required>
  </div>
  <div class="form-group">
    <label for="surname">Apellido:</label>
    <input type="surname" id="surname" name="surname" formControlName="surname" required>
  </div>
  <div>
    <label for="email">Correo electronico:</label>
    <input type="email" id="email" name="email" formControlName="email" required>
  </div>
  <div class="form-group">
    <label for="password">Contraseña:</label>
    <input type="password" id="password" name="password" formControlName="password" required>
  </div>
  <button type="submit">Registrarse</button>
</form>
